.n_boke_card {
  width: 300px;
  margin: 25px 30px !important;
  overflow: hidden;
}
.n_boke_card .card-body {
  position: relative;
  padding: 0;
}
.n_boke_card .card-content {
  position: relative;
  padding: 0.25em 1.25em 1.25em;
  background: #fff;
}
.slant {
  position: absolute;
  right: 0;
  bottom: -75pt;
  left: 0;
  z-index: 0;
  width: 110%;
  min-height: 75pt;
  background-color: #fff;
  -webkit-transform: rotate(7deg) translate(-10px, -20px);
  transform: rotate(7deg) translate(-10px, -20px);
  -ms-transform: rotate(7deg) translate(-10px, -20px);
}
.slant.reverse-slant {
  -webkit-transform: rotate(-10deg) translate(10px, -20px);
  -ms-transform: rotate(-10deg) translate(10px, -20px);
  transform: rotate(-10deg) translate(10px, -20px);
  opacity: 0.7;
  background-color: #f68e5f;
  -webkit-box-shadow: none;
  box-shadow: none;
}
.bg-deepgrey {
  background-color: rgba(0,0,0,0.5) !important;
}
.headImageContainer {
  position: relative;
  overflow: hidden;
}
.headImage {
  position: relative;
  display: inherit;
  min-height: 250px;
  -webkit-transition: -webkit-transform 0.5s ease, filter 0.5s ease;
  -moz-transition: -moz-transform 0.5s ease, filter 0.5s ease;
  transition: transform 0.5s ease, filter 0.5s ease;
  background-position: 50% 50%;
  background-size: cover;
}
.n_boke_card:hover .headImage {
  -webkit-transform: scale(1.1);
  -moz-transform: scale(1.1);
  transform: scale(1.1);
  -webkit-filter: blur(3px);
  -moz-filter: blur(3px);
  filter: blur(3px);
}
.n_boke_card .articleBrief {
  position: absolute;
  top: 0;
  overflow: hidden;
  width: 100%;
  height: 100%;
  padding: 40px 28px;
}
.n_boke_card .articleBrief p {
  font-size: 14px;
  margin: 0;
  padding: 0;
  word-break: break-all;
  opacity: 0;
  color: #fff;
}
.n_boke_card .blanket {
  position: absolute;
  top: 0;
  overflow: hidden;
  width: 100%;
  height: 100%;
  font-size: 60px;
  color: #fff;
  text-align: center;
  line-height: 250px;
}
.n_boke_card:hover .articleBrief {
  background-color: rgba(0,0,0,0.5);
}
.n_boke_card:hover .articleBrief p {
  -webkit-animation: fade-in 0.5s;
  animation: fade-in;
  animation-duration: 0.5s;
  opacity: 1;
}
